package com.idlelong.controller;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RestController;/** * hello控制器 * * @author lizhenlong * @date 2023/12/08 */@RestController@Slf4jpublic class HelloController {    /**     * 打招呼     *     * @param name 名称     * @return {@link String}     */    @GetMapping("/say-hello/{name}")    public String sayHello(@PathVariable String name) {        System.out.println(name);        log.warn(name);        return "你好，" + name + "，欢迎使用Jenkins。";    }}